import jxl.*
import java.io.*
import com.eviware.soapui.model.testsuite.TestRunner.Status

def file = new File("C:\\Users\\SindhuMarar\\Documents\\Sindhu\\SRM\\SDET_Learning\\API\\Project\\Rest\\Login_Credentials.xls")

def workbook = Workbook.getWorkbook(file)

def sh = workbook.getSheet("Sheet1")

def rows = sh.getRows();
def username,password;

log.info rows

for(i=0;i<=rows;i++)
{
	if(i==0)
	{
		username = sh.getCell(0,i).getContents()
		log.info username
	}
	else if(i==1)
	{
		password = sh.getCell(0,i).getContents()
		log.info password
	}
}

// get TestCase
	def tc = testRunner.testCase.testSuite.project.testSuites["Get Token and Login"].testCases["GetAccessToken Login GetAdminUser and Logout"]
	log.info tc

	//set credentials properties from the script itself
	tc.testSteps["TC002_Login"].setPropertyValue("username", username )
	tc.testSteps["TC002_Login"].setPropertyValue("password", password )

	//Run test synchronously
	def runner = tc.run( null, false )

import groovy.sql.Sql
import com.eviware.soapui.model.testsuite.TestRunner.Status

//Connect to database
def url = 'jdbc:mysql://localhost:3306/api_project';
def user = 'root'
def password = 'root'
def driver = 'com.mysql.jdbc.Driver'

//Register to database
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver(driver)

//Connect to sql
def sql = Sql.newInstance(url,user,password,driver)

sql.eachRow("select * from login_credentials")
{
	row->
	def user_name = "$row.username";
	log.info user_name

	def pass_word = "$row.password";
	log.info pass_word

	def tc = testRunner.testCase.testSuite.project.testSuites["Get Token and Login"].testCases["GetAccessToken Login GetAdminUser and Logout"]
	log.info tc

	//set credentials properties from the script itself
	tc.testSteps["TC002_Login"].setPropertyValue("username", user_name )
	tc.testSteps["TC002_Login"].setPropertyValue("password", pass_word )

	//Run test synchronously
	def runner = tc.run( null, false )
}
